import Persons from './components/Persons'
import PersonForm from './components/PersonForm'
import Filter from './components/Filter'
import personService from './services/person'
import { useState, useEffect } from 'react'

const App = () => {


  const [persons, setPersons] = useState([]) 

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')

  useEffect(() => {
    personService.getAll()
                 .then(initPerson => setPersons(initPerson))

  }, [])

  const findNameFunc = (person) => person.name.toUpperCase() === newName.toUpperCase()

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {name: newName,
                          number: newNumber,
                          id: persons.length + 1
                         }

    const checkName = persons.find(findNameFunc)

    if(typeof checkName === 'undefined'){
      personService.create()
                   .then(returnedPerson => {
                      setPersons(persons.concat(returnedPerson))
                      setNewName('')
                      setNewNumber('')
                  })

    }
    else{
      alert(`${newName} is already add to phonebook`)
    }

  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleFilterChange = (event) => {
    setNewFilter(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter newFilter={newFilter} handleFilterChange={handleFilterChange} />

      <h3>add a new</h3>
      <PersonForm addPerson={addPerson} newName={newName} newNumber={newNumber} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange} />

      <h3>Numbers</h3>
      <Persons persons={persons.filter(person =>  person.name.toUpperCase().search(newFilter.toUpperCase()) === -1 ? false : true )} />
    </div>
  )
}

export default App
