import { useState } from 'react'

const Persons = ({persons}) => {
  return (
    <table>
      <tbody>
        {persons.map(person => <Person person={person} key={person.name} />)}
      </tbody> 
    </table> 
  )
}

const Person = ({person}) => <tr>
                                  <td>{person.name}</td>
                                  <td>{person.number}</td>
                             </tr>

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas',
      number: '040-1234567',
      id: 1
    }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  const findNameFunc = (person) => person.name.toUpperCase === newName.toUpperCase

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {name: newName,
                          number: newNumber,
                          id: persons.length + 1
                         }

    const checkName = persons.find(findNameFunc)

    if(typeof checkName === 'undefined'){
      setPersons(persons.concat(personObject))
      setNewName('')
      setNewNumber('')
    }
    else{
      alert(`${newName} is already add to phonebook`)
    }

  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <h2>add a new</h2>
      <form onSubmit={addPerson}>
        <div>
               name: <input value={newName} onChange={handleNameChange} /> <br/>
               number: <input value={newNumber} onChange={handleNumberChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <Persons persons={persons} />
    </div>
  )
}

export default App
