import { useState } from 'react'

const Persons = ({persons}) => {
  return (
    <table>
      <tbody>
        {persons.map(person => <Person person={person} key={person.name} />)}
      </tbody> 
    </table> 
  )
}

const Person = ({person}) => <tr><td>{person.name}</td></tr>

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')

  const addName = (event) => {
    event.preventDefault()
    const nameObject = {name: newName}
    const findNameFunc = (person) => person.name === newName
    nameObject === persons.find(findNameFunc) ? alert({newName}, ' is already add to phonebook') : setPersons(persons.concat(nameObject))
    alert(' is already add to phonebook')
    setNewName('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addName}>
        <div>
          name: <input value={newName} onChange={handleNameChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <Persons persons={persons} />
    </div>
  )
}

export default App
